mapper.insert:
INSERT INTO LYE_COMMENT (

) VALUES (
LYE_COMMENT_SEQ.nextval
, #{writer,jdbcType=VARCHAR}
, TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
, #{contents,jdbcType=VARCHAR}
, #{bid}
)

mapper.select:

select * from (
                  select rownum  as rn, id, contents, bid
                  from (select * from LYE_COMMENT WHERE writer = #{writer, jdbcType=VARCHAR}) where rownum <= #{cri.pageNum} * #{cri.amount} order by id)
where rn > (#{cri.pageNum} -1) * #{cri.amount}
