, #{memberId, jdbcType=VARCHAR}
, #{orderRec, jdbcType=VARCHAR}
, #{addr1, jdbcType=VARCHAR}
, #{addr2, jdbcType=VARCHAR}
, #{addr3, jdbcType=VARCHAR}
, #{phone, jdbcType=VARCHAR}
, #{amount, jdbcType=NUMERIC}
, TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
)

